package com.leetcode;

import java.util.HashMap;
import java.util.Map;

public class FindTheDifference {
    public char findTheDifference(String s, String t) {
        Map<Character, Integer> charMap = new HashMap<>();

        for (char c : s.toCharArray()) {
            charMap.put(c, charMap.getOrDefault(c, 0) + 1);
        }

        for (char c : t.toCharArray()) {
            if (charMap.containsKey(c) && charMap.get(c) != 0)
                charMap.put(c, charMap.get(c) - 1);
            else
                return c;
        }
        return 0;
    }

    public char findTheDifferenceUsingTheXOR(String s, String t) {
        char c = 0;
        for (char cs : s.toCharArray()) c ^= cs;
        for (char ct : t.toCharArray()) c ^= ct;
        return c;
    }

    public static void main(String[] args) {
        String s = "";
        String t = "";

        FindTheDifference fd = new FindTheDifference();

        char c = fd.findTheDifference(s, t);
        System.out.println("Solution using the hash map : " + c);

        c = fd.findTheDifferenceUsingTheXOR(s, t);
        System.out.println("Solution using the XOR : " + c);


    }


}
